/// <reference types="vite/client" />

// Image types
declare module "*.png" {
  const src: string;
  export default src;
}

declare module "*.jpg" {
  const src: string;
  export default src;
}

declare module "*.jpeg" {
  const src: string;
  export default src;
}

declare module "*.gif" {
  const src: string;
  export default src;
}

declare module "*.webp" {
  const src: string;
  export default src;
}

// SVG
declare module "*.svg" {
  const src: string;
  export default src;
}

// PDF
declare module "*.pdf" {
  const src: string;
  export default src;
}